const ll MOD=998244353;
ll QPow(ll x,ll up){
    x=(x+MOD)%MOD;
    ll ans=1;
    while(up)
        if(up%2==0) x=x*x%MOD,up/=2;
        else ans=ans*x%MOD,up--;
    return ans;
}
ll Inv(ll x){
    return QPow(x,MOD-2);
}

const ll PTN=1005;
ll N;
ll fac[PTN],facInv[PTN];
void FacInit(){
    fac[0]=1;for(ll i=1;i&lt;=N;i++) fac[i]=fac[i-1]*i%MOD;
