typedef long long ll;
ll Rd(){
    ll ans=0;char c=getchar();
    while(c&lt;'0'||c&gt;'9') c=getchar();
    while(c&gt;='0'&&c&lt;='9') ans=ans*10+c-'0',c=getchar();
