\begin{gathered}
f = \mu, g = I, h = \varepsilon \\
f = \varphi, g = I, h = \operatorname{id}\\
f = \operatorname{id}^k \mu, g = \operatorname{id}^k, h = \varepsilon \\
f = \operatorname{id}^k \varphi, g = \operatorname{id}^k, h = \operatorname{id}^{k+1}
