#include&lt;iostream&gt;
#include&lt;unordered_set&gt;
typedef long long ll;
struct Vec{
  ll x,y;
    Vec(){}
    Vec(ll x,ll y){
        this-&gt;x=x;this-&gt;y=y;
    }
